import React, { useState } from "react";
import styles from "./Broom.module.scss";

const Broom = () => {
  const [select, setSelect] = useState("broom");

  const hadleChange = (evt) => {
    setSelect(evt.target.id);
  };
  return (
    <section className={styles.broom}>
      <div className="container">
        <div className={styles.box}>
          <div className={styles.inpBox} onChange={(evt) => hadleChange(evt)}>
            <label htmlFor="broom">
              Supurgilar
              <input
                type="radio"
                name="select"
                id="broom"
                className={styles.inp}
                onClick={() => setSelect("broom")}
                de
              />
            </label>
            <label htmlFor="machalka">
              Machalkalar
              <input
                type="radio"
                name="select"
                id="machalka"
                className={styles.inp}
                onClick={() => setSelect("machalka")}
              />
            </label>
            <label htmlFor="bucket">
              Chelaklar
              <input
                type="radio"
                name="select"
                id="bucket"
                className={styles.inp}
                onClick={() => setSelect("bucket")}
              />
            </label>
          </div>
          {select === "broom" ? (
            <ul className={styles.list}>
              <li className={styles.item}>broom</li>
            </ul>
          ) : select === "machalka" ? (
            <ul className={styles.list}>
              <li className={styles.item}>machalka</li>
            </ul>
          ) : (
            <ul className={styles.list}>
              <li className={styles.item}>bucket</li>
            </ul>
          )}
        </div>
      </div>
    </section>
  );
};

export default Broom;
